changeSet.sql:
CREATE SCHEMA IF NOT EXISTS profile; CREATE SCHEMA IF NOT EXISTS profile_service_history; CREATE EXTENSION IF NOT EXISTS "uuid-ossp"; SET search_path TO profile,profile_service_history,public;
CREATE TABLE profile.profiles ( id UUID PRIMARY KEY DEFAULT uuid_generate_v4(), active BOOLEAN NOT NULL DEFAULT TRUE, created TIMESTAMP WITHOUT TIME ZONE NOT NULL DEFAULT (now() AT TIME ZONE 'utc'), updated TIMESTAMP WITHOUT TIME ZONE NOT NULL DEFAULT (now() AT TIME ZONE 'utc'), email VARCHAR(64) NOT NULL, phone_number VARCHAR(16) NOT NULL, first_name VARCHAR(64) NOT NULL, last_name VARCHAR(64) NOT NULL, birth_date DATE NOT NULL, radius INTEGER NOT NULL DEFAULT 10, age_min INTEGER NOT NULL DEFAULT 18, age_max INTEGER NOT NULL DEFAULT 99, interested_in VARCHAR(64) NOT NULL, photos VARCHAR(64)[] );
CREATE TABLE profile_service_history.revinfo ( rev BIGSERIAL PRIMARY KEY, revtmstmp BIGINT );
CREATE TABLE profile_service_history.profiles_history ( id UUID NOT NULL, revision BIGINT NOT NULL, revision_type SMALLINT NOT NULL, active BOOLEAN NOT NULL DEFAULT TRUE, created TIMESTAMP WITHOUT TIME ZONE NOT NULL DEFAULT (now() AT TIME ZONE 'utc'), updated TIMESTAMP WITHOUT TIME ZONE NOT NULL DEFAULT (now() AT TIME ZONE 'utc'), email VARCHAR(64) NOT NULL, phone_number VARCHAR(16) NOT NULL, first_name VARCHAR(64) NOT NULL, last_name VARCHAR(64) NOT NULL, birth_date DATE NOT NULL, radius INTEGER NOT NULL DEFAULT 10, age_min INTEGER NOT NULL DEFAULT 18, age_max INTEGER NOT NULL DEFAULT 99, interested_in VARCHAR(64) NOT NULL, photos VARCHAR(64)[],
CONSTRAINT pk_profiles_history PRIMARY KEY (id, revision), CONSTRAINT fk_profiles_history_rev FOREIGN KEY (revision) REFERENCES profile_service_history.revinfo (rev) );
